import React from 'react';
import Card from '../components/card';
import FormGroup from '../components/form-group'
import { withRouter } from 'react-router-dom'
import Usuario from './app/usuario'

class Login extends React.Component {

  state = {
    email:"",
    senha:"",
    mensagemErro:null
  }

  constructor(){
    super();
    this.service = new Usuario();
  }
  entrar =()=>{
     this.service.autenticar({
      email:this.state.email,
      senha:this.state.senha
     }).then( response => {
       //recuperando usuario logado e transformando em string
       localStorage.setItem('_usuario_logado', JSON.stringify(response.data)) 
       this.props.history.push('/home')
     }).catch( erro => {
        this.setState({mensagemErro: erro.response.data})
     })
  }

  prepareCadastrar = () => {
    this.props.history.push( '/cadastro-usuarios')
  }
  render() {
    return(
     
        <div className="row">
          <div className="col-md-6" style= { { position : 'relative', left: '300px'}}>
              <div className="bs-docs-section">         
                 <Card title="Login">
                 <div className="row">
                    <span>{this.state.mensagemErro}</span>
                 </div>
                    <div className="row">
                      <div className="col-lg-12">
                        <div className="bs-component">
                          <fieldset>
                             <FormGroup label="Email: *" htmlFor="exampleInputEmail1">
                                <input type="email" 
                                       value={this.state.email}
                                       onChange={e => this.setState({email: e.target.value})}
                                       className="form-control" 
                                       id="exampleInputEmail1" aria-describedby="emailHelp"  
                                       placeholder="Digite o Email"/>
                             </FormGroup>
                             <FormGroup label="Senha: *" htmlFor="exampleInputPassword1">
                                <input type="password"
                                       value={this.state.senha}
                                       onChange={e => this.setState({senha: e.target.value})}
                                       className="form-control" 
                                       id="exampleInputPassword1" aria-describedby="senhaHelp"
                                       placeholder="Password"/>
                             </FormGroup>
                             <button onClick={this.entrar} type="button" className="btn btn-success">Entrar</button>
                             <button onClick={this.prepareCadastrar}type="button" className="btn btn-danger">Cadastrar</button>
                          </fieldset>
                        </div>
                      </div>
                    </div>
                 </Card>
              </div>
          </div>
        </div>    
    )
  }
}

export default withRouter( Login )
